// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:5050';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [isLogin, setIsLogin] = useState(true);

  useEffect(() => {
    if (token) {
      fetchTodos();
    }
  }, [token]);

  const fetchTodos = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/todos`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTodos(response.data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  const handleAuth = async (e) => {
    e.preventDefault();
    try {
      const endpoint = isLogin ? '/login' : '/register';
      const data = isLogin ? { username, password } : { username, email, password };
      const response = await axios.post(`${API_BASE_URL}${endpoint}`, data);
      const { access_token } = response.data;
      setToken(access_token);
      localStorage.setItem('token', access_token);
    } catch (error) {
      alert('Authentication failed');
    }
  };

  const addTodo = async () => {
    if (!newTodo.trim()) return;
    try {
      await axios.post(`${API_BASE_URL}/todos`, { title: newTodo }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNewTodo('');
      fetchTodos();
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  const updateTodo = async (id, done) => {
    try {
      await axios.put(`${API_BASE_URL}/todos/${id}`, { done }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchTodos();
    } catch (error) {
      console.error('Error updating todo:', error);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`${API_BASE_URL}/todos/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchTodos();
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem('token');
    setTodos([]);
  };

  if (!token) {
    return (
      <div className="App">
        <div className="auth-container">
          <h1>{isLogin ? 'Login' : 'Register'}</h1>
          <form onSubmit={handleAuth}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            {!isLogin && (
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            )}
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
          </form>
          <button onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? 'Need to register?' : 'Already have an account?'}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <div className="left-red-bar">
        <button className="nav-button" onClick={logout}>Logout</button>
      </div>
      <div className="main-content">
        <h1>My To-Do List</h1>
        <div>
          <input
            type="text"
            placeholder="Add a new task..."
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
          />
          <button onClick={addTodo}>Add Task</button>
        </div>
        <div className="content-boxes-container">
          <div className="content-box">
            <h2>To-Do</h2>
            <ul>
              {todos.filter(todo => !todo.done).map(todo => (
                <li key={todo.id}>
                  {todo.title}
                  <button onClick={() => updateTodo(todo.id, true)}>Done</button>
                  <button onClick={() => deleteTodo(todo.id)}>Delete</button>
                </li>
              ))}
            </ul>
          </div>
          <div className="content-box">
            <h2>Completed Tasks</h2>
            <ul>
              {todos.filter(todo => todo.done).map(todo => (
                <li key={todo.id}>
                  {todo.title}
                  <button onClick={() => updateTodo(todo.id, false)}>Undo</button>
                  <button onClick={() => deleteTodo(todo.id)}>Delete</button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
